const sectionTemplate = `<section class="section" data-section="section">
<h2 class="section-heading"></h2>
<p class="section-content" data-content="content">${pContent}</p>
</section>`;

/**
 * Get the number of sections based on data-section attribute
 * @returns {Number} the number of sections
 */
function getIndex() {
  return sections ? sections().length - 1 : undefined;
}

/**
 * Adds a section at the end of the section container. 
 * Creates a heading for that section.
 * Creates a navigation link for that section
 * Links the navigation link to the section.
 */
function addDataSection() {
  container.insertAdjacentHTML('beforeEnd', sectionTemplate);
  generateHeading();
  generateNavLink();
  addIdsToSection();
}

// Adds a heading to the newly added section based on it's position in sections() array.
function generateHeading() {
  const index = getIndex();
  const heading = sections()[index].querySelector('.section-heading');
  heading.innerHTML = `Section Heading ${index}`;
}

// Adds id to newly added section (last) and updates the respective navigation link reference.
function addIdsToSection() {
  const index = getIndex();
  sections()[index].id = `section${index}`;
  navLinks()[index].href = `#${sections()[index].id}`;
}

// Adds a new navigation link to the view targeting the newly added section (last).
function generateNavLink() {
  const index = getIndex();
  const navList = document.querySelector('.nav-list');
  const navLinkHTML = `<li class="nav-item"><a href="#section${index}" class="nav-link">Section link ${index}</a></li>`;
  navList.innerHTML += navLinkHTML;
}

/**
 * Updates observer. 
 * Track all sections that have an `id` applied
 */
function updateObserver() {
  sections().forEach((section) => {
    observer.observe(section);
  });
  console.log("Update");
}

// Upon clicking adds a new section and updates the observer.
const buttonAdd = document.querySelector('.buttonAdd');
buttonAdd.addEventListener('click', () => {
  addDataSection();
  updateObserver();
});

// Argument for IntersectionObserver.
const options = {
  threshold: 0.5,
};

/**
 * Intersection observer gets triggered when a section come into viewport at a certain height.
 * At that point we add the .active css class to link corresponding to the intersecting section,
 * to indicate the section we are currently viewing on screen.
 */
const observer = new IntersectionObserver(entries => {
  entries.forEach(entry => {
    const id = entry.target.getAttribute('id');
    navLink = document.querySelector(`.nav-link[href="#${id}"]`);
    if (navLink) {
      if (entry.intersectionRatio >= 0.5) {
        console.log(entry.target);
        document.querySelector(`.nav-link[href="#${id}"]`).classList.add('active');
      } else {
        document.querySelector(`nav li a[href="#${id}"]`).classList.remove('active');
      }
    }
    console.log(navLink);
    return;
  });
}, options);

// Update the observer
updateObserver();